<template>
    <a-card :bordered="false">
        <flex-space>
            <img alt="" src="./test.pdf">
            <a-button @click="printTable">打印JSON表格</a-button>
            <a-button @click="printImage">打印图片</a-button>
        </flex-space>
    </a-card>
</template>

<script lang="ts" setup>

import printJS from 'print-js'

defineOptions({ name: 'Proin' })

const printTable = () => {
    printJS({
        printable: [
            { name: 'll', email: '123@gmail.com', phone: '123' },
            { name: 'qq', email: '456@gmail.com', phone: '456' }
        ],
        properties: [ 'name', 'email', 'phone' ],
        type: 'json'
    })
}

const printImage = () => {
    printJS({
        printable: [
            'data:image/jpeg;base64,'
        ],
        type: 'image',
        header: 'Multiple Images',
        imageStyle: 'width:100%;'
    })
}
</script>

